import { Component } from '@angular/core';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent {
  value = "Kushagra"
  disable = false
  greeting=""
  onClick(e:any) {
     this.greeting="Welcome to Devtown"
   }
}
