  @Input() appAlternateNgIF: boolean | any

  constructor(private templateRef: TemplateRef<any>, private vcRef: ViewContainerRef) { 

  }

  ngOnInit(): void {
    if (this.appAlternateNgIF) {
        this.vcRef.createEmbeddedView(this.templateRef)
    }

